INSERT INTO `lm_article` (`id`, `title`, `content`, `create_time`, `status`, `uid`, `is_best`, `is_index`, `sort`, `seo_title`, `seo_keywords`, `seo_desc`, `img`) VALUES
(1, 'PRADA的年轻副线', '<p>\r\n	&nbsp;</p>\r\n<br />\r\n<div>\r\n	<div class="bd_info" style="margin: 14px 0px 0px; padding: 0px 13px 13px; box-shadow: rgba(34, 25, 25, 0.0980392) 0px 0px 1px; -webkit-box-shadow: rgba(34, 25, 25, 0.0980392) 0px 0px 1px; border: 1px solid rgb(230, 228, 228); overflow: hidden; color: rgb(102, 102, 102); font-family: Arial;">\r\n		<h3 class="f16_f m12_0_f" style="margin: 12px 0px 8px; padding: 0px; font-size: 16px;">\r\n			品牌档案</h3>\r\n		<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n			中文名：缪缪</p>\r\n		<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n			英文名：MiuMiu</p>\r\n		<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n			国 家：意大利</p>\r\n		<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n			创建年代：1992年</p>\r\n		<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n			创建人：Miuccia Prada</p>\r\n		<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n			产品系列：女装、男装、鞋履、配饰、手袋、香水、眼镜</p>\r\n		<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n			官方网站：<a class="red_f" href="http://www.miumiu.com/" style="color: rgb(255, 102, 153); text-decoration: none;" target="_blank">www.miumiu.com</a></p>\r\n	</div>\r\n	<div class="bd_info" style="margin: 14px 0px 0px; padding: 0px 13px 13px; box-shadow: rgba(34, 25, 25, 0.0980392) 0px 0px 1px; -webkit-box-shadow: rgba(34, 25, 25, 0.0980392) 0px 0px 1px; border: 1px solid rgb(230, 228, 228); overflow: hidden; color: rgb(102, 102, 102); font-family: Arial;">\r\n		<div class="redline2" style="margin: 0px; padding: 0px; background-color: rgb(255, 102, 153); height: 2px; overflow: hidden; width: 48px; background-position: initial initial; background-repeat: initial initial;">\r\n			&nbsp;</div>\r\n		<h3 class="f16_f m12_0_f" style="margin: 12px 0px 8px; padding: 0px; font-size: 16px;">\r\n			品牌故事</h3>\r\n		<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n			Miu Miu是意大利著名品牌Prada的副线品牌。相信大部份时装迷均知道Prada是由Mario Prada创立的家族生意，以生产手袋闻名欧洲。然而，未必所有时装迷都知道Miu Miu的由来沿革。Miu Miu就是Prada第三代传人Miuccia Prada小姐的昵称。生于1950年的Miuccia小姐，二十岁时已流露了她的聪敏、时装触觉和设计天份，设计了一个采用防水布料&ldquo;Pocone&rdquo;来制造的背包，二十来岁时更考获了政治学博士学位。</p>\r\n	</div>\r\n</div>\r\n<p>\r\n	&nbsp;</p>\r\n', 1372017056, 1, NULL, 0, 0, 1, '', '', '', './public/upload/images/201306/24/51c7c2400d0b4.jpg'),
(2, '前卫又经典的高尚与华贵', '<p>\r\n	&nbsp;</p>\r\n<div class="bd_info" style="margin: 14px 0px 0px; padding: 0px 13px 13px; box-shadow: rgba(34, 25, 25, 0.0980392) 0px 0px 1px; -webkit-box-shadow: rgba(34, 25, 25, 0.0980392) 0px 0px 1px; border: 1px solid rgb(230, 228, 228); overflow: hidden; color: rgb(102, 102, 102); font-family: Arial;">\r\n	<h3 class="f16_f m12_0_f" style="margin: 12px 0px 8px; padding: 0px; font-size: 16px;">\r\n		品牌档案</h3>\r\n	<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n		中文名：圣罗兰</p>\r\n	<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n		英文名：YSL</p>\r\n	<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n		国 家：法国</p>\r\n	<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n		创建年代：1962年</p>\r\n	<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n		创建人：伊夫&middot;圣罗兰 (Yves Saint Laurent)</p>\r\n	<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n		产品系列：男装、女装、手袋、彩妆、香水</p>\r\n	<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n		官方网站：<a class="red_f" href="http://www.ysl.com/" style="color: rgb(255, 102, 153); text-decoration: none;" target="_blank">www.ysl.com</a></p>\r\n</div>\r\n<div class="bd_info" style="margin: 14px 0px 0px; padding: 0px 13px 13px; box-shadow: rgba(34, 25, 25, 0.0980392) 0px 0px 1px; -webkit-box-shadow: rgba(34, 25, 25, 0.0980392) 0px 0px 1px; border: 1px solid rgb(230, 228, 228); overflow: hidden; color: rgb(102, 102, 102); font-family: Arial;">\r\n	<div class="redline2" style="margin: 0px; padding: 0px; background-color: rgb(255, 102, 153); height: 2px; overflow: hidden; width: 48px; background-position: initial initial; background-repeat: initial initial;">\r\n		&nbsp;</div>\r\n	<h3 class="f16_f m12_0_f" style="margin: 12px 0px 8px; padding: 0px; font-size: 16px;">\r\n		品牌故事</h3>\r\n	<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n		上世纪60年代，执时装界牛耳的伊夫圣罗兰先生 (Yves Saint Laurent)，用巴黎左岸最具创新、时尚的独特风情，开创了色彩缤纷、浪漫高雅的Yves Saint Laurent 时代。伊夫圣罗兰 (Yves Saint Laurent) 先生1936年出生于法国属地阿尔及利亚，幼年时便酷爱戏剧，曾设计过舞台布景，同时对素描及绘画有极高的天分，注定会在创作的道路上大放异彩。他17岁时，便被世界知名的时尚杂志&ldquo;Vogue&rdquo;发掘，被誉为神...</p>\r\n</div>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n', 1372017136, 1, NULL, 0, 0, 1, '', '', '', './public/upload/images/201306/24/51c7c27035c90.jpg'),
(3, '乐观精神与东方的诗意美', '<p>\r\n	&nbsp;</p>\r\n<div class="bd_info" style="margin: 14px 0px 0px; padding: 0px 13px 13px; box-shadow: rgba(34, 25, 25, 0.0980392) 0px 0px 1px; -webkit-box-shadow: rgba(34, 25, 25, 0.0980392) 0px 0px 1px; border: 1px solid rgb(230, 228, 228); overflow: hidden; color: rgb(102, 102, 102); font-family: Arial;">\r\n	<h3 class="f16_f m12_0_f" style="margin: 12px 0px 8px; padding: 0px; font-size: 16px;">\r\n		品牌档案</h3>\r\n	<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n		中文名：高田贤三</p>\r\n	<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n		英文名：Kenzo</p>\r\n	<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n		国 家：法国</p>\r\n	<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n		创建年代：1970年</p>\r\n	<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n		创建人：高田贤三</p>\r\n	<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n		产品系列：女装、男装、香水</p>\r\n	<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n		官方网站：<a class="red_f" href="http://www.kenzo.com/" style="color: rgb(255, 102, 153); text-decoration: none;" target="_blank">www.kenzo.com</a></p>\r\n</div>\r\n<div class="bd_info" style="margin: 14px 0px 0px; padding: 0px 13px 13px; box-shadow: rgba(34, 25, 25, 0.0980392) 0px 0px 1px; -webkit-box-shadow: rgba(34, 25, 25, 0.0980392) 0px 0px 1px; border: 1px solid rgb(230, 228, 228); overflow: hidden; color: rgb(102, 102, 102); font-family: Arial;">\r\n	<div class="redline2" style="margin: 0px; padding: 0px; background-color: rgb(255, 102, 153); height: 2px; overflow: hidden; width: 48px; background-position: initial initial; background-repeat: initial initial;">\r\n		&nbsp;</div>\r\n	<h3 class="f16_f m12_0_f" style="margin: 12px 0px 8px; padding: 0px; font-size: 16px;">\r\n		品牌故事</h3>\r\n	<p style="margin: 0px; padding: 0px; line-height: 20px; overflow: hidden;">\r\n		高田贤三 (KENZO) 品牌是出生自日本的设计大师高田贤三 (Kenzo Takada) 创建的时尚品牌。高田贤三 (Kenzo Takada) 是第一个闯荡巴黎的亚洲设计师，更被挑剔的巴黎人誉为&ldquo;时装界的雷诺&rdquo;，他创建的品牌高田贤三 (KENZO) 在70年代即在巴黎立足，并成为令媒体称道的奢侈品品牌，这样的成绩堪称亚洲设计师的典范。<br />\r\n		<br />\r\n		高田贤三 (Kenzo Takada) 大师本人的乐观精神和东方诗意美一直延续在其时装...</p>\r\n</div>\r\n<p>\r\n	&nbsp;</p>\r\n', 1372017178, 1, NULL, 0, 0, 1, '', '', '', './public/upload/images/201306/24/51c7c29ad3403.jpg'),
